CREATE OR REPLACE PROCEDURE pls_altera_fim_valid_processo ( nr_seq_processo_judicial_p bigint, dt_novo_fim_validade_p timestamp, ie_limpar_data_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w			varchar(4000);
nr_seq_tipo_historico_w		bigint;


BEGIN
update	processo_judicial_liminar
set	dt_fim_validade	= CASE WHEN ie_limpar_data_p='S' THEN  null  ELSE dt_novo_fim_validade_p END ,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia 	= nr_seq_processo_judicial_p;

nr_seq_tipo_historico_w	:= coalesce(obter_valor_param_usuario(1337, 9, Obter_Perfil_Ativo, nm_usuario_p, 0),0);

if (nr_seq_tipo_historico_w = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_tipo_historico_w
	from	tipo_histor_proc_judicial;
end if;

if (coalesce(nr_seq_tipo_historico_w, 0) > 0) then
	if (ie_limpar_data_p = 'S') then
		ds_historico_w	:= obter_desc_expressao( 779657 );
	else
		ds_historico_w	:= wheb_mensagem_pck.get_texto(1055440, 'DT=' || to_char(dt_novo_fim_validade_p));
	end if;

	insert into processo_judicial_hist(nr_sequencia,
		nr_seq_processo,
		ds_historico,
		dt_historico,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_tipo_historico,
		ie_permite_alteracao)
	values (nextval('processo_judicial_hist_seq'),
		nr_seq_processo_judicial_p,
		ds_historico_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_tipo_historico_w,
		'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
