CREATE OR REPLACE PROCEDURE pls_altera_fim_vigencia_ocor ( nr_seq_ocor_regra_p pls_ocorrencia_regra.nr_sequencia%type, ie_situacao_p pls_ocorrencia_regra.ie_situacao%type, dt_novo_fim_vigencia_p pls_ocorrencia_regra.dt_fim_vigencia%type, ie_limpar_data_p text, nm_usuario_p pls_ocorrencia_regra.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Alterar a data de fim de vigência e a situação da regra de ocorrência antiga.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

update	pls_ocorrencia_regra
set	dt_fim_vigencia	= CASE WHEN ie_limpar_data_p='S' THEN  null  ELSE dt_novo_fim_vigencia_p END ,
	ie_situacao	= ie_situacao_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia 	= nr_seq_ocor_regra_p;

