SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_altera_status_item_analise ( nr_seq_audit_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_requisicao_w			bigint;
cd_procedimento_w			bigint;
cd_material_ops_w			varchar(255);


BEGIN

begin
	select	b.nr_seq_requisicao,
		a.cd_procedimento,
		substr(pls_obter_seq_codigo_material(a.nr_seq_material,''),1,10)
	into STRICT	nr_seq_requisicao_w,
		cd_procedimento_w,
		cd_material_ops_w
	from	pls_auditoria		b,
		pls_auditoria_item	a
	where	a.nr_sequencia		= nr_seq_audit_item_p
	and	a.nr_seq_auditoria	= b.nr_sequencia;
exception
when others then
	nr_seq_requisicao_w	:= 0;
	cd_procedimento_w	:= null;
	cd_material_ops_w	:= null;
end;

if (coalesce(nr_seq_requisicao_w,0)	<> 0) then
	update	pls_auditoria_item
	set	ie_status	= 'P',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_audit_item_p;

	CALL pls_requisicao_gravar_hist(	nr_seq_requisicao_w, 'L', 'O auditor '||substr(obter_nome_usuario(nm_usuario_p),1,255)||
					' alterou o status do item '||coalesce(cd_procedimento_w,cd_material_ops_w)||' para Pendente em '||
					to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'), null, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
