SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_altera_vinculo_prof ( nr_seq_prestador_p bigint, ie_tipo_vinculo_p text) AS $body$
DECLARE


cd_medico_w			varchar(10);
nr_seq_medico_prest_w		bigint;


BEGIN
if (coalesce(ie_tipo_vinculo_p,'X') <> 'X') then
	select	max(a.cd_pessoa_fisica)
	into STRICT	cd_medico_w
	from	pls_prestador	a
	where	a.nr_sequencia	= nr_seq_prestador_p
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '');

	if (coalesce(cd_medico_w,'X') <> 'X') then
		update	pls_prestador_medico
		set	ie_tipo_vinculo = ie_tipo_vinculo_p
		where	cd_medico 	= cd_medico_w
		and	((coalesce(nr_seq_prestador_ref::text, '') = '') or (nr_seq_prestador_ref = nr_seq_prestador_p));
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
