CREATE OR REPLACE PROCEDURE pls_alter_dt_base_valid_cart ( nr_seq_contrato_p bigint, dt_base_val_cart_nova_p bigint, dt_base_validade_carteira_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_dt_base_carteira_antiga_w	varchar(255);
ds_dt_base_carteira_nova_w	varchar(255);


BEGIN

select	CASE WHEN dt_base_validade_carteira='C' THEN 'Contrato (Mês)' WHEN dt_base_validade_carteira='A' THEN 'Contrato (Ano)' WHEN dt_base_validade_carteira='B' THEN 'Adesão do Beneficiário' WHEN dt_base_validade_carteira='V' THEN 'Data base validade' END ,
	CASE WHEN dt_base_val_cart_nova_p=0 THEN 'Contrato (Mês)' WHEN dt_base_val_cart_nova_p=1 THEN 'Contrato (Ano)' WHEN dt_base_val_cart_nova_p=2 THEN 'Adesão do Beneficiário' WHEN dt_base_val_cart_nova_p=3 THEN 'Data base validade' END
into STRICT	ds_dt_base_carteira_antiga_w,
	ds_dt_base_carteira_nova_w
from	pls_contrato
where	nr_sequencia = nr_seq_contrato_p;

update	pls_contrato
set	dt_base_validade_carteira = CASE WHEN dt_base_val_cart_nova_p=0 THEN 'C' WHEN dt_base_val_cart_nova_p=1 THEN 'A' WHEN dt_base_val_cart_nova_p=2 THEN 'B' WHEN dt_base_val_cart_nova_p=3 THEN 'V' END ,
	dt_base_carteira = CASE WHEN dt_base_validade_carteira_p='' THEN null  ELSE to_date(dt_base_validade_carteira_p) END ,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_sequencia = nr_seq_contrato_p;

insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico,
		ds_historico,ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
		cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '56',
		'Data base da carteira de ' || ds_dt_base_carteira_antiga_w || ' para ' || ds_dt_base_carteira_nova_w,
		'pls_alter_dt_base_valid_cart');

commit;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
