CREATE OR REPLACE PROCEDURE pls_alt_controle_cart_inter ( nr_seq_intercambio_p bigint, ie_controle_carteira_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_controle_carteira_w		varchar(15);
ds_controle_carteira_novo_w	varchar(15);


BEGIN

select	CASE WHEN ie_controle_carteira='V' THEN 'Vencimento' WHEN ie_controle_carteira='E' THEN 'Emissão' WHEN ie_controle_carteira='N' THEN 'Nenhum'  ELSE 'Ambos' END ,
	CASE WHEN ie_controle_carteira_p='V' THEN 'Vencimento' WHEN ie_controle_carteira_p='E' THEN 'Emissão' WHEN ie_controle_carteira_p='N' THEN 'Nenhum'  ELSE 'Ambos' END
into STRICT	ds_controle_carteira_w,
	ds_controle_carteira_novo_w
from	pls_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;

update	pls_intercambio
set	ie_controle_carteira	= ie_controle_carteira_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_intercambio_p;

insert into pls_contrato_historico(	nr_sequencia, cd_estabelecimento, nr_seq_intercambio, dt_historico, ie_tipo_historico,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, ds_historico)
				values (	nextval('pls_contrato_historico_seq'), cd_estabelecimento_p, nr_seq_intercambio_p, clock_timestamp(), '24',
					clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 'Alterado o controle de emissão da carteira de ' || ds_controle_carteira_w || ' para ' || ds_controle_carteira_novo_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
