CREATE OR REPLACE PROCEDURE pls_alt_desc_item_an_nova ( ie_item_p text, nr_seq_proc_mat_p text, ds_alt_item_p text, nm_usuario_p text) AS $body$
DECLARE

qt_registro_regra_w	integer;


BEGIN

if (ie_item_p = 'P') and (nr_seq_proc_mat_p IS NOT NULL AND nr_seq_proc_mat_p::text <> '') then

	select 	count(1)
	into STRICT	qt_registro_regra_w
	from	pls_conta_proc_regra
	where	nr_sequencia = nr_seq_proc_mat_p;

	if (qt_registro_regra_w	= 0) then
		insert into pls_conta_proc_regra(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							ds_item_ptu
					) values (	nr_seq_proc_mat_p,
							clock_timestamp(),
							nm_usuario_p,
							substr(ds_alt_item_p, 1, 255));
	else
		update	pls_conta_proc_regra
		set	ds_item_ptu 	= substr(ds_alt_item_p, 1, 255),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia = nr_seq_proc_mat_p;
	end if;

elsif ( ie_item_p = 'M') and (nr_seq_proc_mat_p IS NOT NULL AND nr_seq_proc_mat_p::text <> '') then

	select 	count(1)
	into STRICT	qt_registro_regra_w
	from	pls_conta_mat_regra
	where	nr_sequencia = nr_seq_proc_mat_p;

	if (qt_registro_regra_w	= 0) then
		insert into pls_conta_mat_regra(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							ds_item_ptu
					) values (	nr_seq_proc_mat_p,
							clock_timestamp(),
							nm_usuario_p,
							substr(ds_alt_item_p, 1, 255));
	else
		update	pls_conta_mat_regra
		set	ds_item_ptu 	= substr(ds_alt_item_p, 1, 255),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia = nr_seq_proc_mat_p;
	end if;

end if;

commit;
end;
$body$
