CREATE OR REPLACE PROCEDURE pls_alt_dt_plano_benef ( nr_seq_alt_plano_p bigint, dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

			
nr_seq_segurado_w	pls_segurado_alt_plano.nr_seq_segurado%type;	
dt_alteracao_w		pls_segurado_alt_plano.dt_alteracao%type;
dt_referencia_w		timestamp;


BEGIN

dt_referencia_w	:= trunc(dt_referencia_p, 'dd');

select 	nr_seq_segurado,
	dt_alteracao
into STRICT 	nr_seq_segurado_w,
	dt_alteracao_w
from 	pls_segurado_alt_plano
where 	nr_sequencia = nr_seq_alt_plano_p;

update 	pls_segurado_alt_plano
set	dt_alteracao 	= dt_referencia_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_alt_plano_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_w,
				4,
				clock_timestamp(),
				'Alteração da data de alteração do produto',
				'Data de alteração do produto alterada de: '|| dt_alteracao_w ||' para: ' || dt_referencia_w,
				null,
				null,
				nr_seq_segurado_w,
				null,
				null,
				null,
				null,
				null,
				null,
				null,
				null,
				nm_usuario_p,
				'N');
				
commit;	

end;
$body$
LANGUAGE PLPGSQL
