CREATE OR REPLACE PROCEDURE pls_alt_dt_resc_solic ( nr_seq_solicitacao_p pls_solicitacao_rescisao.nr_sequencia%type, dt_rescisao_origem_p pls_solicitacao_rescisao.dt_rescisao%type, dt_rescisao_p pls_solicitacao_rescisao.dt_rescisao%type, nr_seq_motivo_rescisao_p bigint, ie_alt_data_sca_p text, ie_alt_data_utilizacao_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_seq_segurado
	from	pls_solic_rescisao_benef
	where	nr_seq_solicitacao	= nr_seq_solicitacao_p;

BEGIN

for r_c01_w in C01 loop
	begin
	CALL pls_alterar_datas(	null, r_c01_w.nr_seq_segurado,null,dt_rescisao_p,19,cd_estabelecimento_p,nr_seq_motivo_rescisao_p,
				ie_alt_data_sca_p,null,ie_alt_data_utilizacao_p,0,'N',nm_usuario_p);
	end;
end loop;

update	pls_solicitacao_rescisao
set	dt_rescisao	= fim_dia(dt_rescisao_p),
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_solicitacao_p;

insert	into	pls_solicitacao_resc_hist(	nr_sequencia, nr_seq_solicitacao, dt_liberacao,
		nm_usuario, nm_usuario_nrec, dt_atualizacao, dt_atualizacao_nrec,
		ds_titulo, ds_historico)
	values (	nextval('pls_solicitacao_resc_hist_seq'), nr_seq_solicitacao_p, clock_timestamp(),
		nm_usuario_p, nm_usuario_p, clock_timestamp(), clock_timestamp(),
		'Alteração de data de rescisão', 'Data de rescisão alterada de '||to_char(dt_rescisao_origem_p,'dd/mm/yyyy')||' para '||to_char(dt_rescisao_p,'dd/mm/yyyy')||'.' );

commit;

end;
$body$
