CREATE OR REPLACE PROCEDURE pls_alt_geracao_valores_contr ( nr_seq_contrato_p bigint, ie_geracao_valores_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_geracao_valores_w	varchar(255);


BEGIN

select	CASE WHEN ie_geracao_valores='B' THEN 'Todos os beneficiários' WHEN ie_geracao_valores='T' THEN 'Titulares' WHEN ie_geracao_valores='D' THEN 'Dependentes' END
into STRICT	ds_geracao_valores_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

update	pls_contrato
set	ie_geracao_valores	= ie_geracao_valores_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_contrato_p;

insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico,
		ds_historico,ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
		cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '50',
		'Geração de valor antigo: ' || ds_geracao_valores_w || ' para gerar o valor : ' || CASE WHEN ie_geracao_valores_p='B' THEN 'Todos os beneficiários' WHEN ie_geracao_valores_p='T' THEN 'Titulares' WHEN ie_geracao_valores_p='D' THEN 'Dependentes' END ,'pls_alt_geracao_valores_contr');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
