SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alt_guia_prest_monit_dupl ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE

		
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Alimentar o campo cd_guia_prestador_ans com numero sequencial
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
		
--localiza nos lotes de envio, todas as contas que tiveram a INC054 de duplicidade de guia gerada.	
ie_modifica_w	varchar(1);

C01 CURSOR FOR
	SELECT	a.nr_seq_conta,
		trunc(d.dt_mes_competencia,'MM') dt_comp
	from	pls_monitor_tiss_cta_val	a,
			pls_monitor_tiss_inc_val	b,
			pls_monitor_tiss_inc		c,
			pls_monitor_tiss_lote d
	where	a.nr_seq_lote_monitor	= nr_seq_lote_p
	and	b.cd_inconsistencia = c.cd_inconsistencia
	and	b.nr_seq_cta_val = a.nr_sequencia
	and  	d.nr_sequencia = a.nr_seq_lote_monitor
	and 	c.cd_inconsistencia 	= 'INC054';

BEGIN

for r_c01_w in C01 loop
	begin
	
	--se ja tem envio aceito em competencia anterior, entao nao muda a guia dessa conta
	select 	CASE WHEN count(1)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_modifica_w
	from	pls_monitor_tiss_alt
	where 	nr_seq_conta = r_c01_w.nr_seq_conta
	and 	dt_evento < r_c01_w.dt_comp
	and 	ie_status = 'A';

	if (ie_modifica_w = 'S') then


		update	pls_conta
		set	cd_guia_prestador_ans	= to_char(r_c01_w.nr_seq_conta),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= r_c01_w.nr_seq_conta
		and	coalesce(cd_guia_prestador_ans::text, '') = '';
	end if;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
