CREATE OR REPLACE PROCEDURE pls_alt_localizacao_benef ( nr_seq_segurado_p bigint, nr_seq_localizacao_benef_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_localizacao_benef_w		bigint;



BEGIN

select	nr_seq_localizacao_benef
into STRICT	nr_seq_localizacao_benef_w
from	pls_segurado
where	nr_sequencia	  = nr_seq_segurado_p;

update 	pls_segurado
set 	nr_seq_localizacao_benef 	= nr_seq_localizacao_benef_p,
	nm_usuario			= nm_usuario_p,
	dt_atualizacao			= clock_timestamp()
where	nr_sequencia			= nr_seq_segurado_p;


/* Gerar histórico */

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '90', clock_timestamp(),
				'Alteração da localização do beneficiário de ' || nr_seq_localizacao_benef_w || ' para ' || nr_seq_localizacao_benef_p,
				'pls_alt_localizacao_benef', null, null,
				null, null, clock_timestamp(),
				null, null, null,
				null, null, null,
				nm_usuario_p, 'S');

commit;

end;
$body$
