CREATE OR REPLACE PROCEDURE pls_alt_motivo_inclusao_benef ( nr_seq_segurado_p bigint, nr_seq_motivo_inclusao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_motivo_inclusao_ant_w	bigint;
nr_seq_portabilidade_w		pls_segurado.nr_seq_portabilidade%type;
ie_portabilidade_w		pls_motivo_inclusao_seg.ie_portabilidade%type;


BEGIN

select	nr_seq_motivo_inclusao,
	nr_seq_portabilidade
into STRICT	nr_seq_motivo_inclusao_ant_w,
	nr_seq_portabilidade_w
from	pls_segurado
where	nr_sequencia		= nr_seq_segurado_p;

update	pls_segurado
set	nr_seq_motivo_inclusao	= nr_seq_motivo_inclusao_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_segurado_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '54', clock_timestamp(), 'Alteração do motivo de inclusão: '|| substr(pls_obter_dados_motivo_incl(nr_seq_motivo_inclusao_ant_w,'D'),1,255) ||
							' para: '||substr(pls_obter_dados_motivo_incl(nr_seq_motivo_inclusao_p,'D'),1,255),
				'pls_alt_motivo_inclusao_benef', null, null, null,
				null, clock_timestamp(), null, null,
				null, null, null, null,
				nm_usuario_p, 'N');							

if (nr_seq_portabilidade_w IS NOT NULL AND nr_seq_portabilidade_w::text <> '') then
	select	coalesce(ie_portabilidade, 'N')
	into STRICT	ie_portabilidade_w
	from	pls_motivo_inclusao_seg
	where	nr_sequencia = nr_seq_motivo_inclusao_p;
	
	if (ie_portabilidade_w = 'N') then
		CALL pls_vincular_solic_portabilid(nr_seq_portabilidade_w, nr_seq_segurado_p, 'D', nm_usuario_p);
	end if;
end if;
				
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
