SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alt_motivo_rescisao_benef ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nr_seq_motivo_rescisao_p bigint, nr_seq_causa_rescisao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_motivo_rescisao_w		pls_motivo_cancelamento.ds_motivo_cancelamento%type;
nr_seq_motivo_rescisao_w	pls_motivo_cancelamento.nr_sequencia%type;
dt_rescisao_w			    pls_segurado.dt_rescisao%type;
nr_seq_segurado_status_w	pls_segurado_status.nr_sequencia%type;
nr_seq_causa_rescisao_w		pls_causa_rescisao.nr_sequencia%type;
ds_causa_rescisao_w			pls_causa_rescisao.ds_causa_rescisao%type;


BEGIN

begin
select	a.nr_sequencia,
		a.ds_motivo_cancelamento,
		b.dt_rescisao
into STRICT	nr_seq_motivo_rescisao_w,
		ds_motivo_rescisao_w,
		dt_rescisao_w
from	pls_segurado b,
		pls_motivo_cancelamento	a
where	a.nr_sequencia = b.nr_seq_motivo_cancelamento
and		b.nr_sequencia = nr_seq_segurado_p
and		a.cd_estabelecimento = cd_estabelecimento_p;
exception
when others then
	nr_seq_motivo_rescisao_w	:= null;
	ds_motivo_rescisao_w		:= wheb_mensagem_pck.get_texto(1046667);
end;	

begin
select	a.nr_sequencia,
		a.ds_causa_rescisao	
into STRICT	nr_seq_causa_rescisao_w,
		ds_causa_rescisao_w				
from    pls_causa_rescisao a,
        pls_segurado b	
where   a.nr_sequencia	 = b.nr_seq_causa_rescisao
and		b.nr_sequencia = nr_seq_segurado_p
and		b.cd_estabelecimento = cd_estabelecimento_p;
exception
when others then
	nr_seq_causa_rescisao_w	:= null;
	ds_causa_rescisao_w		:= wheb_mensagem_pck.get_texto(1046667);
end;


if (coalesce(nr_seq_motivo_rescisao_p,0) <> 0) then
	update	pls_segurado
	set	nr_seq_motivo_cancelamento = nr_seq_motivo_rescisao_p,
		nr_seq_causa_rescisao	= nr_seq_causa_rescisao_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_segurado_p;
	
	select	max(nr_sequencia)
	into STRICT	nr_seq_segurado_status_w
	from	pls_segurado_status
	where	nr_seq_segurado	= nr_seq_segurado_p
	and	dt_final	= fim_dia(dt_rescisao_w)
	and	nr_seq_motivo_cancelamento	= nr_seq_motivo_rescisao_w;
	
	if (nr_seq_segurado_status_w IS NOT NULL AND nr_seq_segurado_status_w::text <> '') then
		update	pls_segurado_status
		set	nr_seq_motivo_cancelamento = nr_seq_motivo_rescisao_p
		where	nr_sequencia    = nr_seq_segurado_status_w;
	end if;
	
	--Gerar historico segurado
	CALL pls_gerar_segurado_historico(
		nr_seq_segurado_p, 47, clock_timestamp(), 'Motivo anterior: ' || ds_motivo_rescisao_w,
		'pls_alt_motivo_rescisao_benef', null,
		null, null, null,
		clock_timestamp(), null, null,
		null, null, null,
		null, nm_usuario_p, 'N');
		
	if (coalesce(nr_seq_causa_rescisao_p,0) <> 0) then
		CALL pls_gerar_segurado_historico(
		nr_seq_segurado_p,82, clock_timestamp(), 'Causa anterior: ' || ds_causa_rescisao_w,
		'pls_alt_motivo_rescisao_benef', null,
		null, null, null,
		clock_timestamp(), null, null,
		null, null, null,
		null, nm_usuario_p, 'N');
	end if;
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_alt_motivo_rescisao_benef ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nr_seq_motivo_rescisao_p bigint, nr_seq_causa_rescisao_p bigint, nm_usuario_p text) FROM PUBLIC;

