CREATE OR REPLACE PROCEDURE pls_alt_produto_dep_sim_preco ( nr_seq_titular_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_dependente_w	bigint;	
nr_seq_simulacao_w	bigint;
nr_seq_produto_w		bigint;
nr_seq_tabela_w		bigint;
				
c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_simulpreco_individual 
	where	nr_seq_simulacao = nr_seq_simulacao_w 
	and	ie_tipo_benef	 = 'D';


BEGIN 
select	nr_seq_simulacao, 
	nr_seq_produto, 
	nr_seq_tabela 
into STRICT	nr_seq_simulacao_w, 
	nr_seq_produto_w, 
	nr_seq_tabela_w 
from	pls_simulpreco_individual 
where	nr_sequencia	= nr_seq_titular_p;	
 
open c01;
loop 
fetch C01 into 
	nr_seq_dependente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	update	pls_simulpreco_individual 
	set	nr_seq_produto	= nr_seq_produto_w, 
		nr_seq_tabela	= nr_seq_tabela_w 
	where	nr_sequencia	= nr_seq_dependente_w;	
end loop;
close C01;
 
CALL pls_recalcular_simulacao(nr_seq_simulacao_w,nm_usuario_p);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_dependente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL pls_gerar_resumo_simulacao(nr_seq_simulacao_w,nr_seq_dependente_w,'E','BS','I',wheb_usuario_pck.get_cd_estabelecimento,nm_usuario_p);
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
