CREATE OR REPLACE PROCEDURE pls_alt_segurado_status ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_acao_p text, dt_anterior_p timestamp, dt_nova_p timestamp, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE


/*	ie_acao_p
	I	Alteração da data inicial (Inclusão, Reainclusão...)
	F	Alteração da data final (Rescisão)
*/
nr_seq_segurado_status_w	pls_segurado_status.nr_sequencia%type;


BEGIN

if (ie_acao_p = 'I') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_segurado_status_w
	from	pls_segurado_status a
	where	a.nr_seq_segurado		= nr_seq_segurado_p
	and	trunc(a.dt_inicial,'dd')	= trunc(dt_anterior_p,'dd')
	and	not exists (	SELECT	1
				from	pls_sib_movimento x
				where	x.nr_seq_status_inclusao = a.nr_sequencia);

	if (nr_seq_segurado_status_w IS NOT NULL AND nr_seq_segurado_status_w::text <> '') then
		update	pls_segurado_status
		set	dt_inicial	= dt_nova_p
		where	nr_sequencia	= nr_seq_segurado_status_w;
	end if;
elsif (ie_acao_p = 'F') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_segurado_status_w
	from	pls_segurado_status a
	where	a.nr_seq_segurado	= nr_seq_segurado_p
	and	trunc(a.dt_final,'dd')	= trunc(dt_anterior_p,'dd')
	and	not exists (	SELECT	1
				from	pls_sib_movimento x
				where	x.nr_seq_status_exclusao = a.nr_sequencia);

	if (nr_seq_segurado_status_w IS NOT NULL AND nr_seq_segurado_status_w::text <> '') then
		update	pls_segurado_status
		set	dt_final	= dt_nova_p
		where	nr_sequencia	= nr_seq_segurado_status_w;
	end if;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
