SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alt_sit_atend_solic_resc ( nr_seq_solicitacao_p bigint, ie_opcao_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_segurado,
		c.ie_situacao_atend
	from	pls_solic_rescisao_benef	a,
		pls_solicitacao_rescisao	b,
		pls_segurado			c,
		pls_motivo_cancelamento		d
	where	b.nr_sequencia	= a.nr_seq_solicitacao
	and	c.nr_sequencia	= a.nr_seq_segurado
	and	d.nr_sequencia	= a.nr_seq_motivo_rescisao
	and	b.nr_sequencia	= nr_seq_solicitacao_p
	and	c.ie_situacao_atend <> 'I'
	and	d.ie_iniciativa_beneficiario = 'S'; --Somente para rescisão por iniciativa do beneficiário
C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_segurado,
		ie_situacao_atend_ant
	from	pls_solic_rescisao_benef
	where	nr_seq_solicitacao	= nr_seq_solicitacao_p
	and	(ie_situacao_atend_ant IS NOT NULL AND ie_situacao_atend_ant::text <> '');
BEGIN

if (ie_opcao_p = 'L') then --Liberar solicitação de rescisão
	for r_c01_w in C01 loop
		begin
		update	pls_solic_rescisao_benef
		set	ie_situacao_atend_ant	= r_c01_w.ie_situacao_atend
		where	nr_sequencia		= r_c01_w.nr_sequencia;

		update	pls_segurado
		set	ie_situacao_atend	= 'S', --Suspenso
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= r_c01_w.nr_seq_segurado;

		CALL pls_gerar_segurado_historico(	r_c01_w.nr_seq_segurado, '29', clock_timestamp(), 'Situação de atendimento suspensa conforme solicitação de rescisão ('||nr_seq_solicitacao_p||')',
						'pls_alt_sit_atend_solic_resc', null, null, null,
						null, clock_timestamp(), null, null,
						null, null, null, null,
						nm_usuario_p, 'N');
		end;
	end loop;
elsif (ie_opcao_p = 'D') then --Desfazer liberação da solicitação de rescisão
	for r_c02_w in C02 loop
		begin
		update	pls_solic_rescisao_benef
		set	ie_situacao_atend_ant	 = NULL
		where	nr_sequencia		= r_c02_w.nr_sequencia;

		update	pls_segurado
		set	ie_situacao_atend	= r_c02_w.ie_situacao_atend_ant,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= r_c02_w.nr_seq_segurado;

		CALL pls_gerar_segurado_historico(	r_c02_w.nr_seq_segurado, '62', clock_timestamp(), 'Situação de atendimento atualizada ao desfazer a liberação da solicitação de rescisão ('||nr_seq_solicitacao_p||')',
						'pls_alt_sit_atend_solic_resc', null, null, null,
						null, clock_timestamp(), null, null,
						null, null, null, null,
						nm_usuario_p, 'N');
		end;
	end loop;
elsif (ie_opcao_p = 'R') then --Rejeitar a solicitação de rescisão
	for r_c02_w in C02 loop
		begin
		update	pls_solic_rescisao_benef
		set	ie_situacao_atend_ant	 = NULL
		where	nr_sequencia		= r_c02_w.nr_sequencia;

		update	pls_segurado
		set	ie_situacao_atend	= r_c02_w.ie_situacao_atend_ant,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= r_c02_w.nr_seq_segurado;

		CALL pls_gerar_segurado_historico(	r_c02_w.nr_seq_segurado, '62', clock_timestamp(), 'Situação de atendimento atualizada ao rejeitar a solicitação de rescisão ('||nr_seq_solicitacao_p||')',
						'pls_alt_sit_atend_solic_resc', null, null, null,
						null, clock_timestamp(), null, null,
						null, null, null, null,
						nm_usuario_p, 'N');
		end;
	end loop;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_alt_sit_atend_solic_resc ( nr_seq_solicitacao_p bigint, ie_opcao_p text, ie_commit_p text, nm_usuario_p text) FROM PUBLIC;

