CREATE OR REPLACE PROCEDURE pls_alt_status_email_processo ( nr_seq_processo_p text, ds_log_p text, nm_usuario_p text) AS $body$
DECLARE



/*
Utilizado na função OPS - Gestão de Envio de Emails
Caso ocorra um erro de processamneto,  gerar um erro para todos os emails vinculados ao processamento

*/
nr_seq_email_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_email
	where	nr_seq_processamento = nr_seq_processo_p;



BEGIN
if ((nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and nr_seq_processo_p <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_email_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert	into  pls_email_log( nr_sequencia, dt_atualizacao,nm_usuario,
					dt_atualizacao_nrec,nm_usuario_nrec,nr_seq_email,ds_log)
		values (nextval('pls_email_log_seq'),clock_timestamp(),nm_usuario_p,
					 clock_timestamp(),nm_usuario_p,nr_seq_email_w,ds_log_p);
		end;
	end loop;
	close C01;

	update	pls_email
	set	ie_status = 'ER',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_seq_processamento = nr_seq_processo_p;



end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
