CREATE OR REPLACE PROCEDURE pls_alt_status_preco_reaj_seg ( nr_seq_processo_p bigint, nr_seq_segurado_preco_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*	ie_opcao_p
	A	Ativar
	I	Inativar
*/
BEGIN

if (ie_opcao_p = 'A') then
	update	pls_segurado_preco
	set	ie_situacao	= 'A',
		nr_seq_processo	= coalesce(nr_seq_processo, nr_seq_processo_p)
	where	nr_sequencia	= nr_seq_segurado_preco_p;
elsif (ie_opcao_p = 'I') then
	update	pls_segurado_preco
	set	ie_situacao	= 'I',
		nr_seq_processo	= coalesce(nr_seq_processo, nr_seq_processo_p)
	where	nr_sequencia	= nr_seq_segurado_preco_p;
end if;

commit;

end;
