CREATE OR REPLACE PROCEDURE pls_alt_tabela_plano_online ( nr_seq_plano_tabela_p pls_regra_simulador_web.nr_sequencia%type, ie_acao_p text, dt_acao_p timestamp, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_plano_online_w		pls_plano_online.nr_sequencia%type;
dt_inicio_vigencia_w		pls_plano_tabela_online.dt_inicio_vigencia%type;
qt_vidas_inicial_w 		pls_plano_tabela_online.qt_vidas_inicial%type;
qt_vidas_final_w 		pls_plano_tabela_online.qt_vidas_final%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		trunc(dt_inicio_vigencia,'dd') dt_inicio_vigencia,
		CASE WHEN coalesce(dt_fim_vigencia::text, '') = '' THEN null  ELSE fim_dia(dt_fim_vigencia) END  dt_fim_vigencia,
		qt_vidas_inicial,
		qt_vidas_final
	from	pls_plano_tabela_online
	where	nr_seq_plano_online	= nr_seq_plano_online_w
	and	nr_sequencia <> nr_seq_plano_tabela_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

BEGIN

if (ie_acao_p = 'L') then --Liberar
	select	nr_seq_plano_online,
		trunc(dt_inicio_vigencia,'dd'),
		qt_vidas_inicial,
		qt_vidas_final
	into STRICT	nr_seq_plano_online_w,
		dt_inicio_vigencia_w,
		qt_vidas_inicial_w,
		qt_vidas_final_w
	from	pls_plano_tabela_online
	where	nr_sequencia	= nr_seq_plano_tabela_p;
	
	for r_c01_w in C01 loop
		begin
		if (coalesce(qt_vidas_inicial_w::text, '') = '') and (coalesce(qt_vidas_final_w::text, '') = '') and (dt_inicio_vigencia_w <= r_c01_w.dt_fim_vigencia or coalesce(r_c01_w.dt_fim_vigencia::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1084175);
		elsif (qt_vidas_inicial_w IS NOT NULL AND qt_vidas_inicial_w::text <> '') and (qt_vidas_final_w IS NOT NULL AND qt_vidas_final_w::text <> '') and
			((qt_vidas_inicial_w between r_c01_w.qt_vidas_inicial and r_c01_w.qt_vidas_final) or (qt_vidas_final_w between r_c01_w.qt_vidas_inicial and r_c01_w.qt_vidas_final)) and (dt_inicio_vigencia_w >= r_c01_w.dt_inicio_vigencia and (dt_inicio_vigencia_w <= r_c01_w.dt_fim_vigencia or coalesce(r_c01_w.dt_fim_vigencia::text, '') = '')) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1084175);
		end if;
		end;
	end loop;
	
	update	pls_plano_tabela_online
	set	dt_liberacao		= clock_timestamp(),
		nm_usuario_liberacao	= nm_usuario_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_plano_tabela_p;
elsif (ie_acao_p = 'F') then --Finalizar vigÃªncia
	update	pls_plano_tabela_online
	set	dt_fim_vigencia	= dt_acao_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_plano_tabela_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
