CREATE OR REPLACE PROCEDURE pls_alt_tipo_comisao_contr ( nr_seq_contrato_p bigint, nr_seq_tipo_comissao_p bigint, cd_estabelecimento_P bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_comissao_w		pls_contrato.nr_seq_tipo_comissao%type;
ds_tipo_comissao_ant_w		pls_tipo_comissao_contrato.ds_tipo_comissao%type;
ds_tipo_comissao_nov_w		pls_tipo_comissao_contrato.ds_tipo_comissao%type;

BEGIN

select	nr_seq_tipo_comissao
into STRICT	nr_seq_tipo_comissao_w
from	pls_contrato
where	nr_sequencia = nr_seq_contrato_p;

if (nr_seq_tipo_comissao_w IS NOT NULL AND nr_seq_tipo_comissao_w::text <> '') then
	select ds_tipo_comissao
	into STRICT ds_tipo_comissao_ant_w
	from pls_tipo_comissao_contrato
	where nr_sequencia = nr_seq_tipo_comissao_w;
end if;

if (nr_seq_tipo_comissao_p IS NOT NULL AND nr_seq_tipo_comissao_p::text <> '') then
	select ds_tipo_comissao
	into STRICT ds_tipo_comissao_nov_w
	from pls_tipo_comissao_contrato
	where nr_sequencia = nr_seq_tipo_comissao_p;
end if;

update	pls_contrato
set	nr_seq_tipo_comissao	= nr_seq_tipo_comissao_p
where	nr_sequencia		= nr_seq_contrato_p;

insert	into	pls_contrato_historico(	nr_sequencia, cd_estabelecimento, nr_seq_contrato,
		dt_historico, ie_tipo_historico, dt_atualizacao,
		nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		ds_historico,ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), cd_estabelecimento_p, nr_seq_contrato_p,
		clock_timestamp(), '74', clock_timestamp(),
		nm_usuario_p, clock_timestamp(), nm_usuario_p,
		'Tipo de Comiss√£o alterado de :' || ds_tipo_comissao_ant_w || ' para : ' || ds_tipo_comissao_nov_w, 'pls_alt_tipo_comisao_contr');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
