SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alt_vigencia_pagador_seg ( nr_seq_seg_pagador_p bigint, dt_inicio_vigencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pagador_antigo_w		bigint;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
dt_fim_vigencia_antiga_w	timestamp;
dt_inicio_vigencia_antiga_w	timestamp;


BEGIN

select	max(nr_seq_segurado)
into STRICT	nr_seq_segurado_w
from	pls_segurado_pagador
where	nr_sequencia	= nr_seq_seg_pagador_p;

-- Pagador anterior, se tiver
select	max(nr_sequencia)
into STRICT	nr_seq_pagador_antigo_w
from	pls_segurado_pagador
where	nr_sequencia < nr_seq_seg_pagador_p
and	nr_seq_segurado = nr_seq_segurado_w;

-- Se tiver mais de um registro na tela ...
if (nr_seq_pagador_antigo_w IS NOT NULL AND nr_seq_pagador_antigo_w::text <> '') then

	-- Seleciona a data do fim de vigência do pagador antigo antes de atualizá-la
	select	dt_fim_vigencia
	into STRICT	dt_fim_vigencia_antiga_w
	from	pls_segurado_pagador
	where	nr_sequencia = nr_seq_pagador_antigo_w;

	-- Atualiza a data do fim de vigência do contrato anterior
	update	pls_segurado_pagador
	set	dt_fim_vigencia = fim_dia(dt_inicio_vigencia_p - 1)
	where	nr_sequencia = nr_seq_pagador_antigo_w;

	insert	into	pls_segurado_pagador_hist(
		nr_sequencia,
		ds_historico,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_historico,
		nm_usuario,
		nm_usuario_historico,
		nm_usuario_nrec,
		nr_seq_seg_pagador)
	values	(
		nextval('pls_segurado_pagador_hist_seq'),
		to_char('Alterada a data de final de vigência de ' || dt_fim_vigencia_antiga_w || ' para ' || (dt_inicio_vigencia_p - 1)),
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_pagador_antigo_w);
end if;

-- Seleciona a data de início de vigência do pagador selecionado antes de atualizá-la
select	dt_inicio_vigencia
into STRICT	dt_inicio_vigencia_antiga_w
from	pls_segurado_pagador
where	nr_sequencia = nr_seq_seg_pagador_p;

-- Atualiza a data de início de vigência do contrato que está sendo alterado
update	pls_segurado_pagador
set	dt_inicio_vigencia = trunc(dt_inicio_vigencia_p,'dd')
where	nr_sequencia = nr_seq_seg_pagador_p;

insert	into	pls_segurado_pagador_hist(
	nr_sequencia,
	ds_historico,
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_historico,
	nm_usuario,
	nm_usuario_historico,
	nm_usuario_nrec,
	nr_seq_seg_pagador)
values	(
	nextval('pls_segurado_pagador_hist_seq'),
	to_char('Alterada a data de início de vigência de ' || dt_inicio_vigencia_antiga_w || ' para ' || (dt_inicio_vigencia_p)),
	clock_timestamp(),
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	nm_usuario_p,
	nm_usuario_p,
	nr_seq_seg_pagador_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
