CREATE OR REPLACE PROCEDURE pls_ame_criar_lote_retorno ( nm_arquivo_p text, dt_lote_p timestamp, nm_usuario_p text, nr_seq_lote_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_retorno_w	pls_ame_regra_retorno.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_regra_retorno_w
from	pls_ame_regra_retorno
where	position(nm_arquivo_contem in nm_arquivo_p) <> 0;

if (coalesce(nr_seq_regra_retorno_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1006042); --Não existe regra aplicável a este lote de retorno, favor criar uma regra aplicável.
end if;

insert	into	pls_ame_lote_retorno(	nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_lote,
		nm_arquivo,
		nr_seq_regra_retorno,
		vl_total,
		dt_importacao)
values (	nextval('pls_ame_lote_retorno_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_lote_p,
		nm_arquivo_p,
		nr_seq_regra_retorno_w,
		0,
		clock_timestamp())
	returning nr_sequencia into nr_seq_lote_p;

commit;

end;
$body$
