CREATE OR REPLACE PROCEDURE pls_analise_alterar_item_conv ( nr_seq_item_p bigint, cd_item_p text, ie_origem_proced_p bigint, ds_item_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w			varchar(20);
ie_tipo_w			w_pls_analise_item.ie_tipo_item%type;
nr_seq_analise_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_conta_proc_w		bigint;


BEGIN

select	max(a.nr_seq_analise),
	max(a.nr_seq_conta_proc),
	max(a.nr_seq_conta_mat),
	max(a.ie_tipo_item)
into STRICT	nr_seq_analise_w,
	nr_seq_conta_proc_w,
	nr_seq_conta_mat_w,
	ie_tipo_w
from	w_pls_analise_item	a
where	a.nr_sequencia	= nr_seq_item_p
and	a.nm_usuario	= nm_usuario_p;


if (nr_seq_analise_w IS NOT NULL AND nr_seq_analise_w::text <> '') then

	if (ie_tipo_w = 'P') then

		update	pls_conta_pos_estabelecido
		set	cd_item_convertido	= cd_item_p,
			ds_item_convertido	= ds_item_p,
			cd_procedimento		= cd_item_p,
			ie_origem_proced	= ie_origem_proced_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_conta_proc	= nr_seq_conta_proc_w
		and	((ie_situacao		= 'A') or (coalesce(ie_situacao::text, '') = ''));

	else
		update	pls_conta_pos_estabelecido
		set	cd_item_convertido	= cd_item_p,
			ds_item_convertido	= ds_item_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_conta_mat	= nr_seq_conta_mat_w
		and	((ie_situacao		= 'A') or (coalesce(ie_situacao::text, '') = ''));
	end if;

	insert into pls_hist_analise_conta(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_analise,
		ie_tipo_historico,
		ds_observacao,
		ds_call_stack)
	values (nextval('pls_hist_analise_conta_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_analise_w,
		34,
		'CÃ³digo do item alterado por: ' || nm_usuario_p,
		substr(dbms_utility.format_call_stack,1,4000));
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
