SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_analise_altera_sit_w_ocor ( nr_sequencia_p bigint, ie_acao_p text, ie_origem_acao_P text default 'N', nm_usuario_p text DEFAULT NULL, nr_seq_analise_p bigint default null) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 

/* IE_ACAO_P
M - Marcar
D - Desmarcar
*/
nr_seq_conta_glosa_w		bigint;
nr_seq_ocor_benef_w		bigint;
cd_motivo_tiss_w		bigint;
ie_glosa_inserida_w		varchar(1)	:= null;
ie_glosar_pagamento_w		varchar(1)	:= null;
ie_glosar_faturamento_w		varchar(1)	:= null;
nr_seq_ocorrencia_ins_w		bigint;
ie_glosar_pagamento_ins_w	varchar(1);
ie_glosar_faturamento_ins_w	varchar(1);
nr_seq_ocorrencia_w		bigint;
ie_permite_w			varchar(3)	:= 'S';
nr_seq_grupo_atual_w		bigint;
nr_seq_analise_w		bigint;
ds_ocorrencia_w			varchar(255);

c01 CURSOR FOR
	SELECT	a.nr_seq_ocorrencia
	from	w_pls_analise_glosa_ocor a
	where	a.nm_usuario	= nm_usuario_p
	and	a.ie_inserido_auditor	= 'S'
	and	(a.nr_seq_ocorrencia IS NOT NULL AND a.nr_seq_ocorrencia::text <> '');


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') then
	select	max(a.nr_seq_conta_glosa),
		max(a.nr_seq_ocor_benef),
		max(a.nr_seq_analise)
	into STRICT	nr_seq_conta_glosa_w,
		nr_seq_ocor_benef_w,
		nr_seq_analise_w
	from	w_pls_analise_glosa_ocor	a
	where	a.nr_sequencia	= nr_sequencia_p
	and	((a.nr_seq_analise = nr_seq_analise_p) or (coalesce(nr_seq_analise_p::text, '') = ''));

	if (nr_seq_conta_glosa_w IS NOT NULL AND nr_seq_conta_glosa_w::text <> '') then
		select	max(b.cd_motivo_tiss)
		into STRICT	cd_motivo_tiss_w
		FROM pls_conta_glosa a
LEFT OUTER JOIN tiss_motivo_glosa b ON (a.nr_seq_motivo_glosa = b.nr_sequencia)
WHERE a.ie_situacao	= 'A'  and a.nr_sequencia		= nr_seq_conta_glosa_w;
	else
		select	max(c.cd_motivo_tiss),
			max(b.nr_sequencia)
		into STRICT	cd_motivo_tiss_w,
			nr_seq_ocorrencia_w
		FROM pls_ocorrencia_benef a, pls_ocorrencia b
LEFT OUTER JOIN tiss_motivo_glosa c ON (b.nr_seq_motivo_glosa = c.nr_sequencia)
WHERE a.nr_seq_ocorrencia	= b.nr_sequencia  and a.nr_sequencia		= nr_seq_ocor_benef_w;
	end if;
	
	/* Obter tipo de ocorrencia, se impede faturamento ou pagamento */

	if (nr_seq_ocor_benef_w IS NOT NULL AND nr_seq_ocor_benef_w::text <> '') then
		select	a.ie_glosar_pagamento,
			a.ie_glosar_faturamento
		into STRICT	ie_glosar_pagamento_w,
			ie_glosar_faturamento_w
		from	pls_ocorrencia a,
			pls_ocorrencia_benef b
		where	a.nr_sequencia = b.nr_seq_ocorrencia
		and	b.nr_sequencia = nr_seq_ocor_benef_w;
		
		open C01;
		loop
		fetch C01 into	
			nr_seq_ocorrencia_ins_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			select	a.ie_glosar_pagamento,
				a.ie_glosar_faturamento
			into STRICT	ie_glosar_pagamento_ins_w,
				ie_glosar_faturamento_ins_w
			from	pls_ocorrencia a
			where	a.nr_sequencia = nr_seq_ocorrencia_ins_w;
			
			/* Teve que ser feito esse tratamento, devido a poder inserir mais de uma na tela do F2*/

			if (ie_glosar_pagamento_ins_w = 'S') and (ie_glosar_faturamento_ins_w = 'S') and (coalesce(ie_glosa_inserida_w,'X') <> 'A') then
				ie_glosa_inserida_w	:= 'A'; /* Ambos, Interfere pagamento e faturamento */
			elsif (ie_glosar_pagamento_ins_w = 'S') and (coalesce(ie_glosa_inserida_w,'X') <> 'F') and (coalesce(ie_glosa_inserida_w,'X') <> 'A') then
				ie_glosa_inserida_w	:= 'P'; /* Pagamento */
			elsif (ie_glosar_faturamento_ins_w = 'S') and (coalesce(ie_glosa_inserida_w,'X') <> 'P') and (coalesce(ie_glosa_inserida_w,'X') <> 'A') then
				ie_glosa_inserida_w	:= 'F'; /* Faturamento */
			end if;
			end;
		end loop;
		close C01;
	end if;
	
	select	max(a.nr_seq_grupo)
	into STRICT	nr_seq_grupo_atual_w
	from	pls_auditoria_conta_grupo a
	where	a.nr_seq_analise	= nr_seq_analise_w
	and	a.nm_auditor_atual	= nm_usuario_p
	and	coalesce(a.dt_liberacao::text, '') = '';
	
	if (nr_seq_grupo_atual_w IS NOT NULL AND nr_seq_grupo_atual_w::text <> '') then
		ie_permite_w	:= pls_obter_se_nivel_lib_auditor(nr_seq_ocorrencia_w,nr_seq_grupo_atual_w,nm_usuario_p,'S');
	end if;
	
	if (ie_permite_w = 'S') then

		update	w_pls_analise_glosa_ocor
		set	ie_selecionada	= CASE WHEN ie_acao_p='M' THEN 'S'  ELSE 'N' END ,
			ie_situacao	= CASE WHEN ie_acao_p='M' THEN 'A'  ELSE 'I' END
		where	nr_sequencia	= nr_sequencia_p;
	
	elsif (ie_origem_acao_P  = 'N') then

		select	max(a.ds_ocorrencia)
		into STRICT	ds_ocorrencia_w
		from	pls_ocorrencia a
		where	a.nr_sequencia	= nr_seq_ocorrencia_w;
	
		CALL wheb_mensagem_pck.exibir_mensagem_abort(236616,'DS_OCORRENCIA=' || ds_ocorrencia_w, -20012);
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_analise_altera_sit_w_ocor ( nr_sequencia_p bigint, ie_acao_p text, ie_origem_acao_P text default 'N', nm_usuario_p text DEFAULT NULL, nr_seq_analise_p bigint default null) FROM PUBLIC;

