CREATE OR REPLACE PROCEDURE pls_analise_atualizar_benef ( nr_seq_analise_p bigint, nr_seq_segurado_p bigint, nr_seq_grupo_atual_p bigint, nr_seq_fatura_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_conta_w			bigint;
ie_preco_w			pls_plano.ie_preco%type;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_conta	a 
	where	a.nr_seq_analise	= nr_seq_analise_p;				
 

BEGIN 
 
select	max(b.ie_preco) 
into STRICT	ie_preco_w 
from	pls_segurado	a, 
	pls_plano	b 
where	a.nr_seq_plano	= b.nr_sequencia 
and	a.nr_sequencia 	= nr_seq_segurado_p;
 
update	pls_conta 
set	nr_seq_segurado = nr_seq_segurado_p, 
	dt_atualizacao	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p 
where	nr_seq_analise = nr_seq_analise_p;
 
update	pls_analise_conta 
set	nr_seq_segurado	= nr_seq_segurado_p, 
	nm_segurado 	= pls_obter_dados_segurado(nr_seq_segurado_p, 'N'), 
	dt_atualizacao	= clock_timestamp(), 
	ie_preco	= coalesce(ie_preco_w,ie_preco), 
	nm_usuario	= nm_usuario_p 
where	nr_sequencia 	= nr_seq_analise_p;
 
CALL pls_consistir_analise(nr_seq_analise_p, nr_seq_grupo_atual_p, cd_estabelecimento_p, nm_usuario_p);
 
CALL ptu_ajustar_fatura_pcmso(nr_seq_fatura_p, nr_seq_segurado_p, cd_estabelecimento_p, nm_usuario_p, 'N');
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
