SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_analise_concluir_solic ( nr_seq_solicitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_analise_w		bigint;
qt_solic_aberta_w		integer	:= 0;


BEGIN

/* Obter dados da solicitação */

select	nr_seq_analise
into STRICT	nr_seq_analise_w
from	pls_analise_solicitacao
where	nr_sequencia	= nr_seq_solicitacao_p;

update	pls_analise_solicitacao
set	dt_conclusao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_solicitacao_p;

select	count(*)
into STRICT	qt_solic_aberta_w
from	pls_analise_solicitacao
where	nr_seq_analise	= nr_seq_analise_w
and	coalesce(dt_conclusao::text, '') = '';

if (qt_solic_aberta_w	= 0) then
	update	pls_analise_adesao
	set	ie_status	= 'C'
	where	nr_sequencia	= nr_seq_analise_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
