SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_analise_desfazer_solic ( nr_seq_analise_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_solicitacao_w		bigint;
nr_seq_proposta_w		bigint;
qt_propostas_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_solicitacao
	where	nr_seq_analise	= nr_seq_analise_p;



BEGIN

select	nr_seq_proposta
into STRICT	nr_seq_proposta_w
from	pls_analise_adesao
where	nr_sequencia = nr_seq_analise_p;

select	count(*)
into STRICT	qt_propostas_w
from	pls_proposta_adesao
where	nr_sequencia = nr_seq_proposta_w
and	ie_status	in ('A','C');

/*Proposta da análise foi aprovada ou contratada. Não pode ser desfeito as solicitações da análise!*/

if (qt_propostas_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(256652);
end if;

open C01;
loop
fetch C01 into
	nr_seq_solicitacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_analise_solicitacao
	set	dt_conclusao	 = NULL,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_solicitacao_w;

	end;
end loop;
close C01;

update	pls_analise_adesao
set	ie_status	= 'B'
where	nr_sequencia	= nr_seq_analise_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
