SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_analise_enc_itens_grupo ( nr_seq_analise_p bigint, nr_seq_w_item_p bigint, nr_seq_grupo_atual_p bigint, nr_seq_grupo_p bigint, nr_seq_ocorrencia_p bigint, ds_observacao_p text, ie_prox_ant_p bigint, nr_id_transacao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Encaminhar para an?lise de outro grupo auditor (An?lise Nova)
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicion?rio [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relat?rios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de aten??o:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_status_analise_w		varchar(10);
ie_tipo_linha_w			varchar(10);
nr_seq_item_w			bigint;
nr_seq_conta_w			bigint;
nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_proc_partic_w		bigint;
ie_tipo_item_w			bigint;
nr_seq_ocor_benef_w		bigint;
nr_seq_segurado_w		bigint;
qt_analise_parc_w		bigint;
nr_seq_ordem_ant_w		bigint;
nr_seq_ordem_atual_w		bigint;
nr_seq_ordem_novo_w		bigint;
dt_finalizacao_w		timestamp;
nr_seq_fluxo_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_seq_w_item
	from	w_pls_analise_selecao_item a
	where	a.nr_seq_analise	= nr_seq_analise_p
	
union

	SELECT	nr_seq_w_item_p
	
	where	(nr_seq_w_item_p IS NOT NULL AND nr_seq_w_item_p::text <> '');
	

BEGIN
if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') and (nr_seq_grupo_atual_p IS NOT NULL AND nr_seq_grupo_atual_p::text <> '') then
	select	a.nr_seq_segurado
	into STRICT	nr_seq_segurado_w
	from	pls_analise_conta a
	where	a.nr_sequencia	= nr_seq_analise_p;
	
	if (coalesce(nr_seq_grupo_p::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(210211);
	end if;
	
	if (coalesce(nr_seq_ocorrencia_p::text, '') = '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(210213);
	end if;

	if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
		open C01;
		loop
		fetch C01 into	
			nr_seq_item_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			/* Obter dados */

			begin
			select	a.nr_seq_conta,
				a.nr_seq_conta_proc,
				a.nr_seq_conta_mat,
				a.nr_seq_proc_partic,
				a.ie_status_analise,
				a.ie_tipo_linha
			into STRICT	nr_seq_conta_w,
				nr_seq_conta_proc_w,
				nr_seq_conta_mat_w,
				nr_seq_proc_partic_w,
				ie_status_analise_w,
				ie_tipo_linha_w
			from	w_pls_analise_item a
			where	a.nr_sequencia	= nr_seq_item_w
			and		a.nr_id_transacao = nr_id_transacao_p;
			exception
				when others then
				nr_seq_conta_w		:= null;
				nr_seq_conta_proc_w	:= null;
				nr_seq_conta_mat_w	:= null;
				nr_seq_proc_partic_w	:= null;
			end;
			
			ie_tipo_item_w	:= null;
			
			if (nr_seq_conta_proc_w IS NOT NULL AND nr_seq_conta_proc_w::text <> '') or (nr_seq_proc_partic_w IS NOT NULL AND nr_seq_proc_partic_w::text <> '') then
				ie_tipo_item_w	:= 3;
			elsif (nr_seq_conta_mat_w IS NOT NULL AND nr_seq_conta_mat_w::text <> '') then
				ie_tipo_item_w	:= 4;
			elsif (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then
				ie_tipo_item_w	:= 8;
			end if;
			
			if (nr_seq_proc_partic_w IS NOT NULL AND nr_seq_proc_partic_w::text <> '') then
				nr_seq_conta_proc_w	:= null;
			end if;
			
			/* N?o considerar t?tulos e linhas informativas */

			if (ie_tipo_item_w IS NOT NULL AND ie_tipo_item_w::text <> '') and (coalesce(ie_tipo_linha_w,'X')	<> 'T') and (coalesce(ie_status_analise_w,'X')	<> 'I') then
			
				nr_seq_ocor_benef_w := pls_inserir_ocorrencia(	nr_seq_segurado_w, nr_seq_ocorrencia_p, null, null, nr_seq_conta_w, nr_seq_conta_proc_w, nr_seq_conta_mat_w, null, nm_usuario_p, ds_observacao_p, null, ie_tipo_item_w, cd_estabelecimento_p, 'N', null, nr_seq_ocor_benef_w, null, nr_seq_proc_partic_w, 'S', null);
							
				update	pls_ocorrencia_benef
				set	ie_encaminhamento	= 'S'
				where	nr_sequencia		= nr_seq_ocor_benef_w;
				
				insert into pls_analise_glo_ocor_grupo(nr_sequencia,
					nm_usuario,
					dt_atualizacao,
					nm_usuario_nrec,
					dt_atualizacao_nrec,
					nr_seq_analise,
					nr_seq_ocor_benef,
					nr_seq_grupo,
					ie_status,
					ie_fluxo_gerado,
					nr_seq_fluxo)
				values (nextval('pls_analise_glo_ocor_grupo_seq'),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nr_seq_analise_p,
					nr_seq_ocor_benef_w,
					nr_seq_grupo_p,
					'P',
					'S',
					nr_seq_fluxo_w);
			end if;
						
			end;
		end loop;
		close C01;
	end if;
	
	select	count(1)
	into STRICT	qt_analise_parc_w
	from	pls_analise_pedido_parecer
	where	nr_seq_analise		= nr_seq_analise_p
	and	nr_seq_grupo_parecer	= nr_seq_grupo_p
	and	nr_seq_grupo_atual	= nr_seq_grupo_atual_p
	and	coalesce(dt_finalizacao::text, '') = '';
	
	/* Finalizar os pareceres anteriores */

	if (qt_analise_parc_w > 0) then
		update	pls_analise_pedido_parecer
		set	dt_finalizacao		= clock_timestamp()
		where	nr_seq_analise		= nr_seq_analise_p
		and	nr_seq_grupo_parecer	= nr_seq_grupo_p
		and	nr_seq_grupo_atual	= nr_seq_grupo_atual_p;
	end if;
	
	select	max(nr_seq_ordem)
	into STRICT	nr_seq_ordem_atual_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_grupo	= nr_seq_grupo_atual_p
	and	nr_seq_analise	= nr_seq_analise_p;
	
	if (ie_prox_ant_p = 0) then
		nr_seq_ordem_novo_w	:= nr_seq_ordem_atual_w - 1;
	else
		nr_seq_ordem_novo_w	:= nr_seq_ordem_atual_w + 1;
	end if;

	select	max(dt_final_analise),
		max(nr_seq_ordem)
	into STRICT	dt_finalizacao_w,
		nr_seq_ordem_ant_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_grupo	= nr_seq_grupo_p
	and	nr_seq_analise	= nr_seq_analise_p;

	insert into pls_analise_pedido_parecer(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_grupo_atual,
		nr_seq_grupo_parecer,
		dt_finalizacao_anterior,
		nr_seq_fluxo_anterior,
		nr_seq_analise,
		dt_finalizacao,
		nr_seq_novo_fluxo)
	values (nextval('pls_analise_pedido_parecer_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_grupo_atual_p,
		nr_seq_grupo_p,
		dt_finalizacao_w,
		nr_seq_ordem_ant_w,
		nr_seq_analise_p,
		null,
		nr_seq_ordem_novo_w);
		
	ds_retorno_p	:= 'Parecer solicitado com sucesso.';
	
	CALL pls_analise_gerar_grupos_enc(nr_seq_analise_p, nr_seq_grupo_atual_p, 'N', cd_estabelecimento_p, ie_prox_ant_p, nr_seq_grupo_p, ds_observacao_p, nm_usuario_p);

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_analise_enc_itens_grupo ( nr_seq_analise_p bigint, nr_seq_w_item_p bigint, nr_seq_grupo_atual_p bigint, nr_seq_grupo_p bigint, nr_seq_ocorrencia_p bigint, ds_observacao_p text, ie_prox_ant_p bigint, nr_id_transacao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) FROM PUBLIC;

