CREATE OR REPLACE PROCEDURE pls_analise_inserir_ocor_selec ( nr_seq_analise_p bigint, nr_seq_ocorrencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
nr_sequencia_w			bigint;
nr_seq_conta_w			bigint;
nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_proc_partic_w		bigint;
nr_seq_ocor_benef_w		bigint;
nr_seq_conta_glosa_w		bigint;
ie_tipo_linha_w			varchar(3);
ie_status_analise_w		varchar(5);
nr_seq_conta_pos_estab_w	bigint;
nr_seq_glosa_conta_w		bigint;

/* Itens selecionados */
 
c01 CURSOR FOR 
	SELECT	a.nr_seq_w_item 
	from	w_pls_analise_selecao_item a 
	where	a.nr_seq_analise	= nr_seq_analise_p 
	and	a.nm_usuario		= nm_usuario_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	begin 
	select	a.nr_seq_conta, 
		a.nr_seq_conta_proc, 
		a.nr_seq_conta_mat, 
		a.nr_seq_proc_partic, 
		a.ie_status_analise, 
		a.ie_tipo_linha, 
		a.nr_seq_conta_pos_estab 
	into STRICT	nr_seq_conta_w, 
		nr_seq_conta_proc_w, 
		nr_seq_conta_mat_w, 
		nr_seq_proc_partic_w, 
		ie_status_analise_w, 
		ie_tipo_linha_w, 
		nr_seq_conta_pos_estab_w 
	from	w_pls_analise_item a 
	where	a.nr_sequencia	= nr_sequencia_w;
	exception 
		when others then 
		nr_seq_conta_w		:= null;
		nr_seq_conta_proc_w	:= null;
		nr_seq_conta_mat_w	:= null;
		nr_seq_proc_partic_w	:= null;
	end;
	 
	/* Não considerar títulos e linhas informativas */
 
	if (coalesce(ie_tipo_linha_w,'X') <> 'T') and (coalesce(ie_status_analise_w,'X') <> 'I') then 
		if (nr_seq_proc_partic_w IS NOT NULL AND nr_seq_proc_partic_w::text <> '') then 
			nr_seq_conta_proc_w	:= null;
		end if;
		 
		if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') or (nr_seq_conta_proc_w IS NOT NULL AND nr_seq_conta_proc_w::text <> '') or (nr_seq_conta_mat_w IS NOT NULL AND nr_seq_conta_mat_w::text <> '') or (nr_seq_proc_partic_w IS NOT NULL AND nr_seq_proc_partic_w::text <> '') then 
			nr_seq_glosa_conta_w := pls_analise_inserir_glosa_item(nr_seq_analise_p, nr_seq_conta_w, nr_seq_conta_proc_w, nr_seq_conta_mat_w, nr_seq_proc_partic_w, nr_seq_ocorrencia_p, null, 'N', cd_estabelecimento_p, nm_usuario_p, 'N', nr_seq_glosa_conta_w);
		end if;
	end if;
	 
	CALL pls_gerar_w_analise_selec_item(nr_seq_analise_p, nr_sequencia_w, null, 'D', nm_usuario_p, 'N');
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
