CREATE OR REPLACE PROCEDURE pls_analise_manter_itens_selec (nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, nr_seq_w_item_p bigint, cd_estabelecimento_p bigint, ds_parecer_p text, nm_usuario_p text, nr_id_transacao_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Manter o parecer dos itens de análise selecionados
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_item_w		bigint;
nr_seq_conta_w		bigint;
nr_seq_conta_proc_w	bigint;
nr_seq_conta_mat_w	bigint;
nr_seq_proc_partic_w	bigint;
ie_status_analise_w	varchar(10);
ie_tipo_linha_w		varchar(10);

c01 CURSOR FOR
	SELECT	a.nr_seq_w_item
	from	w_pls_analise_selecao_item a
	where	a.nr_seq_analise	= nr_seq_analise_p
	and	a.nm_usuario		= nm_usuario_p
	
union

	SELECT	nr_seq_w_item_p
	
	where	(nr_seq_w_item_p IS NOT NULL AND nr_seq_w_item_p::text <> '');


BEGIN
if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') and (nr_seq_grupo_atual_p IS NOT NULL AND nr_seq_grupo_atual_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		begin
		select	a.nr_seq_conta,
			a.nr_seq_conta_proc,
			a.nr_seq_conta_mat,
			a.nr_seq_proc_partic,
			a.ie_status_analise,
			a.ie_tipo_linha
		into STRICT	nr_seq_conta_w,
			nr_seq_conta_proc_w,
			nr_seq_conta_mat_w,
			nr_seq_proc_partic_w,
			ie_status_analise_w,
			ie_tipo_linha_w
		from	w_pls_analise_item a
		where	a.nr_sequencia	= nr_seq_item_w
		and	a.nr_id_transacao = nr_id_transacao_p;
		exception
			when others then
			nr_seq_conta_w		:= null;
			nr_seq_conta_proc_w	:= null;
			nr_seq_conta_mat_w	:= null;
			nr_seq_proc_partic_w	:= null;
		end;

		/* Não considerar títulos e linhas informativas */

		if (coalesce(ie_tipo_linha_w,'X') <> 'T') and (coalesce(ie_status_analise_w,'X') not in ('I', 'C')) then
			CALL pls_analise_manter_parecer(nr_seq_analise_p,
						nr_seq_conta_w,
						nr_seq_conta_proc_w,
						nr_seq_conta_mat_w,
						nr_seq_proc_partic_w,
						nr_seq_grupo_atual_p,
						cd_estabelecimento_p,
						ds_parecer_p,
						'N',
						nm_usuario_p);
		end if;

		CALL pls_gerar_w_analise_selec_item(nr_seq_analise_p, nr_seq_item_w, null, 'D', nm_usuario_p, 'N', nr_id_transacao_p);
		end;
	end loop;
	close C01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
