SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_analise_parecer_item_disc (nr_sequencia_p bigint, nr_seq_motivo_glosa_aceita_p bigint, nr_seq_motivo_glosa_negada_p bigint, ds_parecer_p text, ie_selecionado_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p text, ds_observacao_p text default null) AS $body$
DECLARE


qt_contestada_w		double precision;
vl_contestado_w		double precision;
nr_seq_conta_w		bigint;
nr_seq_analise_w	bigint;
ie_tipo_item_w		varchar(1);
ds_observacao_w		varchar(4000);
ds_motivo_w		    varchar(255);
nr_seq_item_w		  bigint;
qt_aceita_w		    double precision;
vl_aceito_w		    double precision;
qt_negada_w		    double precision;
vl_negado_w		    double precision;


BEGIN
select	max(nr_seq_item)
into STRICT	nr_seq_item_w
from	w_pls_discussao_item
where	nr_sequencia = nr_sequencia_p;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_selecionado_p = 1) then
	select	coalesce(qt_contestada,0),
		coalesce(vl_contestado,0)
	into STRICT	qt_contestada_w,
		vl_contestado_w
	from	w_pls_discussao_item
	where	nr_sequencia = nr_sequencia_p;

	select	max(nr_seq_conta),
		max(nr_seq_analise),
		max(ie_tipo_item)
	into STRICT	nr_seq_conta_w,
		nr_seq_analise_w,
		ie_tipo_item_w
	from	w_pls_discussao_item
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_motivo_glosa_aceita_p IS NOT NULL AND nr_seq_motivo_glosa_aceita_p::text <> '') then		/* Aceito */
		select	max(ds_motivo)
		into STRICT	ds_motivo_w
		from	pls_motivo_glosa_aceita
		where	nr_sequencia = 	nr_seq_motivo_glosa_aceita_p;

		update	w_pls_discussao_item
		set	qt_aceita			= qt_contestada_w,
			vl_aceito			= vl_contestado_w,
			ie_situacao			= 'A'
		where	nr_sequencia			= nr_sequencia_p;

		update	w_pls_discussao_item
		set	ie_situacao 	= 'A'
		where	nr_seq_item	= nr_seq_item_w
		and	(nr_seq_partic_proc IS NOT NULL AND nr_seq_partic_proc::text <> '');

		select	coalesce(qt_negada,0),		/* Verifica se ja houve negacao */
			coalesce(vl_negado,0)
		into STRICT	qt_negada_w,
			vl_negado_w
		from	w_pls_discussao_item
		where	nr_sequencia = nr_sequencia_p;

		if (qt_negada_w + vl_negado_w > 0) then
			update	w_pls_discussao_item
			set	qt_negada			= 0,
				vl_negado			= 0
			where	nr_sequencia			= nr_sequencia_p;
		end if;

		insert into pls_analise_parecer_disc	/* Parecer */
			(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_parecer,
			nr_seq_w_disc_item,
			nr_seq_motivo_aceite,
			nr_seq_motivo_neg)
		values (nextval('pls_analise_parecer_disc_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_parecer_p,
			nr_sequencia_p,
			nr_seq_motivo_glosa_aceita_p,
			null);

    	if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then

		insert into pls_conta_observacao_a500	/* Observacao*/
			(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_observacao,
			nr_seq_conta,
			nr_seq_conta_proc,
			nr_seq_conta_mat)
            
            
		values (nextval('pls_conta_observacao_a500_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_observacao_p,
			nr_seq_conta_w,
			CASE WHEN ie_tipo_item_w='M' THEN  null  ELSE nr_seq_item_w END ,
      CASE WHEN ie_tipo_item_w='M' THEN  nr_seq_item_w  ELSE null END );

            end if;


		ds_observacao_w := 	'Tipo de liberacao:  '||chr(13)||chr(10)||
					chr(9)||'Liberacaoo favoravel'||chr(13)||chr(10)||chr(13)||chr(10)||
					'Motivo de liberacao:'||chr(13)||chr(10)||
					chr(9)||ds_motivo_w||chr(13)||chr(10)||chr(13)||chr(10)||
					'Observacao/Parecer: '||chr(13)||chr(10)||
					chr(9)||ds_parecer_p;

		/* Historico */

		CALL pls_inserir_hist_analise(nr_seq_conta_w,nr_seq_analise_w,25,nr_sequencia_p,ie_tipo_item_w,null,null,ds_observacao_w,nr_seq_grupo_p,
					nm_usuario_p,cd_estabelecimento_p);

	elsif (nr_seq_motivo_glosa_negada_p IS NOT NULL AND nr_seq_motivo_glosa_negada_p::text <> '') then		/* Negado */
		select	max(ds_motivo)
		into STRICT	ds_motivo_w
		from	pls_motivo_glosa_negada
		where	nr_sequencia = 	nr_seq_motivo_glosa_negada_p;

		update	w_pls_discussao_item
		set	qt_negada			= qt_contestada_w,
			vl_negado			= vl_contestado_w,
			ie_situacao			= 'N'
		where	nr_sequencia			= nr_sequencia_p;

		update	w_pls_discussao_item
		set	ie_situacao 	= 'N'
		where	nr_seq_item	= nr_seq_item_w
		and	(nr_seq_partic_proc IS NOT NULL AND nr_seq_partic_proc::text <> '');

		select	coalesce(qt_aceita,0),		/* Verifica se ja houve aceitacao */
			coalesce(vl_aceito,0)
		into STRICT	qt_aceita_w,
			vl_aceito_w
		from	w_pls_discussao_item
		where	nr_sequencia = nr_sequencia_p;

		if (qt_aceita_w + vl_aceito_w > 0) then
			update	w_pls_discussao_item
			set	qt_aceita			= 0,
				vl_aceito			= 0
			where	nr_sequencia			= nr_sequencia_p;
		end if;

		insert into pls_analise_parecer_disc	/* Parecer */
			(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_parecer,
			nr_seq_w_disc_item,
			nr_seq_motivo_aceite,
			nr_seq_motivo_neg)
		values (nextval('pls_analise_parecer_disc_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_parecer_p,
			nr_sequencia_p,
			null,
			nr_seq_motivo_glosa_negada_p);

		insert into pls_conta_observacao_a500	/* Observacao*/
			(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_observacao,
			nr_seq_conta,
			nr_seq_conta_proc,
			nr_seq_conta_mat)
		values (nextval('pls_conta_observacao_a500_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_observacao_p,
			nr_seq_conta_w,
			CASE WHEN ie_tipo_item_w='M' THEN  null  ELSE nr_seq_item_w END ,
      CASE WHEN ie_tipo_item_w='M' THEN  nr_seq_item_w  ELSE null END );

		ds_observacao_w := 	'Tipo de liberacao:  '||chr(13)||chr(10)||
					chr(9)||'Liberacao desfavoravel'||chr(13)||chr(10)||chr(13)||chr(10)||
					'Motivo de liberacao:'||chr(13)||chr(10)||
					chr(9)||ds_motivo_w||chr(13)||chr(10)||chr(13)||chr(10)||
					'Observacao/Parecer: '||chr(13)||chr(10)||
					chr(9)||ds_parecer_p;

		/* Historico */

		CALL pls_inserir_hist_analise(nr_seq_conta_w,nr_seq_analise_w,26,nr_sequencia_p,ie_tipo_item_w,null,null,ds_observacao_w,nr_seq_grupo_p,
          nm_usuario_p,cd_estabelecimento_p);

  end if;

commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_analise_parecer_item_disc (nr_sequencia_p bigint, nr_seq_motivo_glosa_aceita_p bigint, nr_seq_motivo_glosa_negada_p bigint, ds_parecer_p text, ie_selecionado_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p text, ds_observacao_p text default null) FROM PUBLIC;

