CREATE OR REPLACE PROCEDURE pls_analise_vincular_proc_mat ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, nr_seq_proc_mat_p bigint, ie_tipo_item_p text, cd_item_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_item_w			varchar(255);
nr_seq_conta_w			bigint;


BEGIN
if (ie_tipo_item_p	= 'P') then
	begin
	update	pls_conta_proc
	set	cd_procedimento		= cd_item_p,
		ie_origem_proced	= ie_origem_proced_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_proc_mat_p;
	exception
	when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(190443);
	end;

	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_proc_mat_p;
elsif (ie_tipo_item_p	= 'M') then
	update	pls_conta_mat
	set	nr_seq_material		= cd_item_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_proc_mat_p;

	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_conta_mat
	where	nr_sequencia	= nr_seq_proc_mat_p;
end if;

--pls_consistir_analise(nr_seq_analise_p, nr_seq_grupo_p, cd_estabelecimento_p, nm_usuario_p);
commit;

end;
$body$
