SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_aplicao_nao_reajuste ( nr_seq_reajuste_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_reajuste
where	nr_sequencia		= nr_seq_reajuste_p
and	coalesce(ie_reaj_nao_aplicado,'N')	= 'N'
and	ie_status		= '1';

if (qt_registros_w > 0) then
	update	pls_reajuste
	set	ie_reaj_nao_aplicado	= 'S',
		ie_status		= '2'
	where	nr_sequencia		= nr_seq_reajuste_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
