CREATE OR REPLACE PROCEDURE pls_aplicar_reaj_benef (nr_seq_lote_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/*

'A' - Aplicar reajuste
'D' - Desfazer aplicação reajuste

*/
BEGIN

update	pls_segurado_preco
set	dt_liberacao		= CASE WHEN ie_acao_p='A' THEN  clock_timestamp()  ELSE null END ,
	nm_usuario_liberacao	= CASE WHEN ie_acao_p='A' THEN  nm_usuario_p  ELSE null END
where	nr_seq_lote		= nr_seq_lote_p;

update	pls_segurado_preco_origem
set	dt_liberacao		= CASE WHEN ie_acao_p='A' THEN  clock_timestamp()  ELSE null END ,
	nm_usuario_liberacao	= CASE WHEN ie_acao_p='A' THEN  nm_usuario_p  ELSE null END
where	nr_seq_lote_reajuste	= nr_seq_lote_p;

update	pls_lote_reaj_segurado
set	ie_status		= CASE WHEN ie_acao_p='A' THEN  '2'  ELSE '3' END ,
	nm_usuario_liberacao	= CASE WHEN ie_acao_p='A' THEN  nm_usuario_p  ELSE null END ,
	dt_liberacao		= CASE WHEN ie_acao_p='A' THEN  clock_timestamp()  ELSE null END
where	nr_sequencia		= nr_seq_lote_p;

update	pls_segurado a
set	a.ie_tipo_valor	= CASE WHEN ie_acao_p='A' THEN 'A'  ELSE 'I' END
where	exists (	SELECT	1
			from	pls_segurado_preco x
			where	a.nr_sequencia	= x.nr_seq_segurado
			and	x.nr_seq_lote	= nr_seq_lote_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
