CREATE OR REPLACE PROCEDURE pls_aplicar_reaj_programado ( nr_seq_lote_prog_reaj_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_prog_reajuste_w	bigint;
nr_seq_grupo_w		bigint;
nr_seq_reajuste_w	bigint;
tx_reajuste_w		double precision;
dt_reajuste_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_grupo,
		coalesce(tx_reaj_proposto, tx_reaj_sugerida)
	from	pls_prog_reajuste
	where	nr_seq_lote	= nr_seq_lote_prog_reaj_p
	and	(nm_usuario_liberacao IS NOT NULL AND nm_usuario_liberacao::text <> '')
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(nr_seq_reajuste::text, '') = '';


BEGIN

select	dt_reajuste
into STRICT	dt_reajuste_w
from	pls_prog_reajuste_lote
where	nr_sequencia	= nr_seq_lote_prog_reaj_p;

open C01;
loop
fetch C01 into
	nr_seq_prog_reajuste_w,
	nr_seq_grupo_w,
	tx_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('pls_reajuste_seq')
	into STRICT	nr_seq_reajuste_w
	;

	insert	into	pls_reajuste(	nr_sequencia, cd_estabelecimento, nr_seq_prog_reajuste, nr_seq_grupo_contrato,
			dt_reajuste, tx_reajuste, ie_indice_reajuste,
			ie_tipo_reajuste, dt_periodo_inicial, dt_periodo_final,
			ie_status, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, ie_aplicacao_reajuste,
			ie_reajustar_inscricao, ie_reajustar_vl_manutencao, ie_reajustar_copartic, ie_reajustar_via_adic,
			ie_reaj_nao_aplicado, ie_tipo_contrato, ie_reajuste_tabela,DT_REAJUSTE_AUX)
		values (	nr_seq_reajuste_w, cd_estabelecimento_p, nr_seq_prog_reajuste_w, nr_seq_grupo_w,
			dt_reajuste_w, tx_reajuste_w, '3',
			'C', dt_reajuste_w, fim_mes(add_months(dt_reajuste_w,11)),
			'1', clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, 'G',
			'N', 'N', 'N', 'N',
			'N', 'O', 'A',to_char(dt_reajuste_w,'mm/yyyy'));

	update	pls_prog_reajuste
	set	nr_seq_reajuste	= nr_seq_reajuste_w
	where	nr_sequencia	= nr_seq_prog_reajuste_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
