CREATE OR REPLACE PROCEDURE pls_aplicar_renovacao_via_adic ( nr_seq_contrato_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_dias_w		bigint;


BEGIN

qt_dias_w	:= coalesce(obter_valor_param_usuario(1202,49,Obter_Perfil_Ativo, nm_usuario_p,0),'0');

if (qt_dias_w <> 0) then
		insert into pls_carteira_renovacao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				cd_estabelecimento,qt_meses_renovacao,nr_seq_contrato,ie_tipo_renovacao)
		values (	nextval('pls_carteira_renovacao_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				cd_estabelecimento_p,qt_dias_w,nr_seq_contrato_p,'V');
end if;

end;
