CREATE OR REPLACE PROCEDURE pls_aprovar_cotacao ( nr_seq_item_cotacao_p bigint, nr_seq_ret_cotacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Aprovar a cotação dos itens
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (nr_seq_item_cotacao_p IS NOT NULL AND nr_seq_item_cotacao_p::text <> '') and (nr_seq_ret_cotacao_p IS NOT NULL AND nr_seq_ret_cotacao_p::text <> '') then

	update	pls_retorno_cotacao
	set	ie_selecionado	= 'S',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_ret_cotacao_p;

	update	pls_item_cotacao
	set	ie_status	= 4,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_item_cotacao_p;

	insert into pls_item_cotacao_hist(
			nr_sequencia, dt_atualizacao, ie_tipo_historico,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			nr_seq_item_cotacao, ds_historico)
	values (
			nextval('pls_item_cotacao_hist_seq'), clock_timestamp(), 1,
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nr_seq_item_cotacao_p, 'O usuário '||nm_usuario_p||' aprovou a cotação.');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
