SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_aprovar_repasse ( nr_seq_repasse_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_referencia_w			timestamp;
ie_mes_fechado_w		varchar(1);
ie_lote_contabil_w		varchar(1);


BEGIN

select 	dt_referencia
into STRICT	dt_referencia_w
from 	pls_repasse_vend
where	nr_sequencia = nr_seq_repasse_p;

select	pls_obter_se_mes_fechado(dt_referencia_w,'T', cd_estabelecimento_p)
into STRICT	ie_mes_fechado_w
;

if (ie_mes_fechado_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(224762);
	/* Mensagem: Não é possível realizar esta operação pois o mês de competência está fechado! */

end if;

ie_lote_contabil_w	:= obter_se_lote_contabil_gerado(24, dt_referencia_w);

if (ie_lote_contabil_w = 'S') then
	/* Não é possível concluir a ação executada pois existe um lote contábil gerado para a competência #@DT_COMPETENCIA#@! */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(458973, 'DT_COMPETENCIA=' || to_char(dt_referencia_w,'mm/yyyy'));
end if;

update	pls_repasse_vend
set	dt_aprovacao		= trunc(clock_timestamp()),
	nm_usuario_aprov	= nm_usuario_p,
	nm_usuario			= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia	= nr_seq_repasse_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
