SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_assumir_grupo_auditoria ( nr_seq_grupo_auditor bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(3);
nr_seq_auditoria_w		bigint;
nm_usuario_exec_w		varchar(15);


BEGIN

begin
	select	nm_usuario_exec
	into STRICT	nm_usuario_exec_w
	from	pls_auditoria_grupo
	where	nr_sequencia	= nr_seq_grupo_auditor;
exception
when others then
	nm_usuario_exec_w	:= null;
end;

if (coalesce(nm_usuario_exec_w::text, '') = '') then
	begin
		select	nr_seq_auditoria
		into STRICT	nr_seq_auditoria_w
		from	pls_auditoria_grupo
		where	nr_sequencia	= nr_seq_grupo_auditor;
	exception
	when others then
		nr_seq_auditoria_w	:= null;
	end;

	if (nr_seq_auditoria_w IS NOT NULL AND nr_seq_auditoria_w::text <> '') then
		begin
			select	ie_status
			into STRICT	ie_status_w
			from	pls_auditoria
			where	nr_sequencia	= nr_seq_auditoria_w;
		exception
		when others then
			ie_status_w	:= null;
		end;

		if (ie_status_w	in ('F','C')) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(230314);
		end if;
	end if;


	update	pls_auditoria_grupo
	set	nm_usuario_exec		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_inicio_auditoria    	= clock_timestamp()
	where	nr_sequencia	= nr_seq_grupo_auditor;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
