CREATE OR REPLACE PROCEDURE pls_atend_evt_agenda_externa (nr_seq_atend_evento_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_atendimento_w	pls_atendimento.nr_sequencia%type;


BEGIN

select	nr_seq_atendimento
into STRICT	nr_seq_atendimento_w
from	pls_atendimento_evento a
where	a.nr_sequencia 	= nr_seq_atend_evento_p;

update	pls_atendimento_evento a
set	dt_atualizacao	= clock_timestamp(),
	ie_status	= 'C',
	nm_usuario	= nm_usuario_p,
	dt_fim_evento	= clock_timestamp()
where	not exists (	SELECT 	1
			from	pls_atend_evt_agenda_ext x
			where	x.nr_seq_atend_evento = a.nr_sequencia
			and	coalesce(x.dt_agendada::text, '') = ''
			and	coalesce(x.dt_cancelamento::text, '') = '')
and	a.nr_sequencia = nr_seq_atend_evento_p;

update	pls_atendimento a
set	ie_status 	= 'G', /*Aguardando Conclusão*/
	nm_usuario 	= nm_usuario_p,
	dt_atualizacao 	= clock_timestamp()
where	not exists (	SELECT	1
				from	pls_atendimento_evento x
				where	x.nr_seq_atendimento = a.nr_sequencia
				and	x.ie_status = 'P')
and	a.nr_sequencia 	= nr_seq_atendimento_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
