CREATE OR REPLACE PROCEDURE pls_ativar_inativar_grupo ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_w	bigint;
qt_inativos_w	bigint;
qt_ativos_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_grupo_contrato
	where	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_inativos_w
	from	pls_contrato_grupo	a,
		pls_contrato		b
	where	nr_seq_grupo	= nr_seq_grupo_w
	and	b.nr_sequencia	= a.nr_seq_contrato
	and	(b.dt_rescisao_contrato IS NOT NULL AND b.dt_rescisao_contrato::text <> '');

	select	count(*)
	into STRICT	qt_ativos_w
	from	pls_contrato_grupo	a,
		pls_contrato		b
	where	nr_seq_grupo	= nr_seq_grupo_w
	and	b.nr_sequencia	= a.nr_seq_contrato;

	if (qt_ativos_w	= qt_inativos_w) then
		update	pls_grupo_contrato
		set	ie_situacao	= 'I',
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_grupo_w;
	else
		update	pls_grupo_contrato
		set	ie_situacao	= 'A',
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_grupo_w;
	end if;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
