SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ativar_status_usuario_even ( cd_pessoa_fisica_p text, nm_usuario_p text, ie_duplicidade_pf_p INOUT text) AS $body$
DECLARE


ie_duplicidade_pf_w		varchar(10);
nm_pessoa_fisica_w		varchar(100);
dt_nascimento_w			timestamp;
nr_identidade_w			varchar(100);
ie_consistiu_duplic_w		varchar(4000);


BEGIN

ie_duplicidade_pf_w	:= 'N';

/*Consistência para a função OPS - Identificação de Beneficiários*/

if (wheb_usuario_pck.GET_CD_FUNCAO = 1273) then
	select	nm_pessoa_fisica,
		dt_nascimento,
		nr_identidade
	into STRICT	nm_pessoa_fisica_w,
		dt_nascimento_w,
		nr_identidade_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	ie_consistiu_duplic_w := pls_Consistir_Duplic_Cada_PF(cd_pessoa_fisica_p, nm_pessoa_fisica_w, dt_nascimento_w, nr_identidade_w, nm_usuario_p, ie_consistiu_duplic_w);

	if (ie_consistiu_duplic_w = 'S') then

		/*aaschlote  - 11/12/2012 - Caso achou duplicidade para a pessoa, então coloca a mesma na tabela*/

		insert into W_PLS_DUPLICIDADE_PF(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				cd_pessoa_fisica)
		values (	nextval('w_pls_duplicidade_pf_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				cd_pessoa_fisica_p);

		ie_duplicidade_pf_w	:= 'S';
	end if;
end if;

update	pessoa_fisica
set	ie_status_usuario_event	= 'A',
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

ie_duplicidade_pf_p	:= ie_duplicidade_pf_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
