CREATE OR REPLACE PROCEDURE pls_att_dt_inicio_vig_pag ( nr_seq_pagador_p bigint, dt_inicio_vigencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_seg_pagador_w	pls_segurado_pagador.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado_pagador
	where	nr_seq_pagador = nr_seq_pagador_p
	and	trunc(dt_inicio_vigencia,'dd') > trunc(dt_inicio_vigencia_p,'dd');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_seg_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_alt_vigencia_pagador_seg(nr_seq_seg_pagador_w, dt_inicio_vigencia_p, nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
