CREATE OR REPLACE PROCEDURE pls_atualizar_analise_result ( dt_mes_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

	 
dt_referencia_w		timestamp;
	
C01 CURSOR FOR 
	SELECT	to_date('01/' || dt_mes_competencia_aux) 
	from	pls_competencia 
	where	coalesce(dt_fechamento::text, '') = '' 
	and	dt_mes_competencia_aux = to_char(clock_timestamp(),'mm/yyyy');
					

BEGIN 
/* Atualizar normalmente pela data solicitada */
 
CALL pls_gerar_margem_beneficiario(dt_mes_referencia_p,cd_estabelecimento_p,nm_usuario_p);
 
/* Atualizar pelos meses em aberto */
 
open C01;
loop 
fetch C01 into	 
	dt_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_gerar_margem_beneficiario(dt_referencia_w,cd_estabelecimento_p,nm_usuario_p);
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
