CREATE OR REPLACE PROCEDURE pls_atualizar_auditor_grupo ( nr_seq_grupo_p bigint, nr_seq_analise_p bigint, nm_usuario_p text ) AS $body$
BEGIN
 
/* 
Diego - 05/07/2011 - OS 330395 
Esta rotina possui a finalidade de obter se o grupo dentro da análise (passados por parâmetro) já possui um auditor que não é o usuário. 
 
Se nm_auditor_atual_p for preenchido então existe um auditor atualmente no grupo (este estará informado no parâmetro). 
*/
 
 
update	pls_auditoria_conta_grupo 
set	nm_auditor_atual	= nm_usuario_p, 
	dt_inicio_auditor	= clock_timestamp(), 
	dt_atualizacao		= clock_timestamp(), 
	nm_usuario		= nm_usuario_p 
where	nr_sequencia		= (	SELECT	max(nr_sequencia) 
					from	pls_auditoria_conta_grupo 
					where	nr_seq_grupo 		= nr_seq_grupo_p	 
					and	nr_seq_analise		= nr_seq_analise_p 
					and	coalesce(dt_liberacao::text, '') = '' 
					and	nr_seq_ordem 		= (	select	min(nr_seq_ordem)		 
										from	pls_auditoria_conta_grupo 
										where	nr_seq_grupo 		= nr_seq_grupo_p	 
										and	nr_seq_analise		= nr_seq_analise_p 
										and	coalesce(dt_liberacao::text, '') = ''	) 
				  );
				  
CALL pls_gravar_inicio_fim_analise(nr_seq_analise_p, nr_seq_grupo_p, 'I', nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
