CREATE OR REPLACE PROCEDURE pls_atualizar_campos_cart_emis ( nr_seq_cart_emis_p bigint, nr_seq_carterinha_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_solicitacao_w		timestamp;
dt_validade_carteira_w		timestamp;
nm_usuario_solic_w		varchar(15);
ds_observacao_w			varchar(255);
ds_trilha1_w			pls_segurado_carteira.ds_trilha1%type;
ds_trilha2_w			pls_segurado_carteira.ds_trilha2%type;
ds_trilha3_w			pls_segurado_carteira.ds_trilha3%type;
cd_matricula_est_w		varchar(30);


BEGIN

select	a.dt_solicitacao,
	a.dt_validade_carteira,
	a.nm_usuario_solicitante,
	a.ds_observacao,
	a.ds_trilha1,
	a.ds_trilha2,
	a.ds_trilha3,
	b.cd_matricula_estipulante
into STRICT	dt_solicitacao_w,
	dt_validade_carteira_w,
	nm_usuario_solic_w,
	ds_observacao_w,
	ds_trilha1_w,
	ds_trilha2_w,
	ds_trilha3_w,
	cd_matricula_est_w
from	pls_segurado		b,
	pls_segurado_carteira	a
where	a.nr_seq_segurado	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_carterinha_p;

update	pls_carteira_emissao
set	dt_solicitacao		= dt_solicitacao_w,
	dt_validade_carteira	= dt_validade_carteira_w,
	nm_usuario_solic	= nm_usuario_solic_w,
	ds_observacao		= ds_observacao_w,
	ds_trilha_1		= ds_trilha1_w,
	ds_trilha_2		= ds_trilha2_w,
	ds_trilha_3		= ds_trilha3_w,
	cd_matricula_est	= cd_matricula_est_w
where	nr_sequencia		= nr_seq_cart_emis_p;

end;
