CREATE OR REPLACE PROCEDURE pls_atualizar_carteira_plano (nr_atendimento_p bigint, dt_inicio_vigencia_p timestamp, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_pessoa_fisica_p text, dt_atendimento_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, carteiras_selec_p INOUT text) AS $body$
DECLARE


ds_procedure_w		varchar(255);
cd_usuario_plano_w	varchar(255);
dt_validade_w		timestamp;


BEGIN

if (coalesce(cd_convenio_p,0) <> 0) then

	ds_procedure_w := Obter_Regra_Valid_Usuario_Conv(cd_convenio_p, cd_categoria_p, ie_tipo_atendimento_p, ds_procedure_w);

	if (upper(ds_procedure_w) = 'PLS_OBTER_USUARIO_PLANO') then

		SELECT * FROM pls_obter_usuario_plano(cd_pessoa_fisica_p, dt_atendimento_p, cd_estabelecimento_p, cd_usuario_plano_w, dt_validade_w) INTO STRICT cd_usuario_plano_w, dt_validade_w;

		if (coalesce(cd_usuario_plano_w,'X') <> 'X') and (position(',' in cd_usuario_plano_w) = 0) then

			update	atend_categoria_convenio
			set	cd_usuario_convenio	= cd_usuario_plano_w,
				dt_validade_carteira	= dt_validade_w,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	nr_atendimento		= nr_atendimento_p
			and	cd_convenio		= cd_convenio_p
			and	cd_categoria		= cd_categoria_p
			and	dt_inicio_vigencia	= dt_inicio_vigencia_p;

			select	null
			into STRICT	carteiras_selec_p
			;

		else

			select	cd_usuario_plano_w
			into STRICT	carteiras_selec_p
			;

		end if;
	end if;
end if;

commit;

end;
$body$
