SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_cart_ant_sib ( nr_seq_retorno_sib_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_usuario_plano_ant_w		varchar(30);
nr_seq_segurado_w		bigint;
qt_cart_ant_w			bigint;


BEGIN

select	a.cd_usuario_plano,
	a.nr_seq_segurado_vinculado
into STRICT	cd_usuario_plano_ant_w,
	nr_seq_segurado_w
from	pls_retorno_sib a,
	pls_segurado b
where	a.nr_seq_segurado_vinculado	= b.nr_sequencia
and	a.nr_sequencia			= nr_seq_retorno_sib_p;

select	count(*)
into STRICT	qt_cart_ant_w
from	pls_segurado_cart_ant
where	nr_seq_segurado	= nr_seq_segurado_w
and	ie_sistema_anterior	= 'S'
and	cd_usuario_ant		= cd_usuario_plano_ant_w;

if (qt_cart_ant_w	= 0) then

	update	pls_segurado_cart_ant
	set	ie_sistema_anterior	= 'N'
	where	nr_seq_segurado		= nr_seq_segurado_w;

	insert into pls_segurado_cart_ant(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_usuario_ant,
						nr_seq_segurado,
						dt_alteracao,
						ds_observacao,
						ie_status_carteira,
						ie_sistema_anterior,
						nm_usuario_solicitacao,
						dt_solicitacao)
					values (	nextval('pls_segurado_cart_ant_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_usuario_plano_ant_w,
						nr_seq_segurado_w,
						clock_timestamp(),
						'Alteração gerada pelo registro da base da ANS',
						'A',
						'S',
						nm_usuario_p,
						clock_timestamp());
end if;

update	pls_retorno_sib
set	ie_carteira_anterior	= 'S'
where	nr_sequencia	= nr_seq_retorno_sib_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
