SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_contas_receita ( nr_seq_lote_p bigint, nr_seq_mensalidade_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commit_p text, ie_geracao_p INOUT text) AS $body$
DECLARE


ie_esquema_contabil_w	varchar(1);
dt_referencia_w		timestamp	:= null;
qt_lote_gerado_mes_w	bigint;
nr_contador_w		bigint      := 0;
nr_seq_mens_seg_w	pls_mensalidade_segurado.nr_sequencia%type;
ie_contab_ppcng_w	pls_parametro_contabil.ie_contab_ppcng%type;
ie_performance_contab_w	pls_visible_false.ie_performance_contab%type;
qt_movimento_w		bigint;

c_receita CURSOR FOR
	SELECT	s.nr_sequencia nr_seq_mensalidade_seg
	from	pls_lote_mensalidade		l,
		pls_mensalidade			m,
		pls_mensalidade_segurado	s
	where	l.nr_sequencia	= m.nr_seq_lote	
	and	m.nr_sequencia	= s.nr_seq_mensalidade
	and	coalesce(l.cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and	l.nr_sequencia	= coalesce(nr_seq_lote_p,l.nr_sequencia)
	and	m.nr_sequencia	= coalesce(nr_seq_mensalidade_p,m.nr_sequencia);


BEGIN

select	coalesce(max(ie_performance_contab), 'N')
into STRICT	ie_performance_contab_w
from	pls_visible_false
where	cd_estabelecimento = cd_estabelecimento_p;

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	select	max(dt_mesano_referencia)
	into STRICT	dt_referencia_w
	from	pls_lote_mensalidade
	where	nr_sequencia	= nr_seq_lote_p;
elsif (nr_seq_mensalidade_p IS NOT NULL AND nr_seq_mensalidade_p::text <> '') then
	select	max(a.dt_mesano_referencia)
	into STRICT	dt_referencia_w
	from	pls_mensalidade	b,
		pls_lote_mensalidade a
	where	b.nr_seq_lote	= a.nr_sequencia
	and	b.nr_sequencia	= nr_seq_mensalidade_p;
end if;
if (coalesce(dt_referencia_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(273984);
end if;	

if (dt_referencia_w IS NOT NULL AND dt_referencia_w::text <> '') then
	dt_referencia_w	:= trunc(dt_referencia_w,'month');
	
	select	count(1)
	into STRICT	qt_lote_gerado_mes_w
	from	lote_contabil
	where	cd_tipo_lote_contabil	in (21,49,50)
	and	trunc(dt_referencia,'month') = dt_referencia_w
	and	(dt_geracao_lote IS NOT NULL AND dt_geracao_lote::text <> '')
	and	cd_estabelecimento = cd_estabelecimento_p;

	if (qt_lote_gerado_mes_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 188714, 'DT_REFERENCIA=' || to_char(dt_referencia_w,'mm/yyyy') );
	end if;
end if;

begin
select	coalesce(ie_esquema_contabil,'N'),
	coalesce(ie_contab_ppcng,'N')
into STRICT	ie_esquema_contabil_w,
	ie_contab_ppcng_w
from	pls_parametro_contabil
where	cd_estabelecimento	= cd_estabelecimento_p;
exception
when others then
	ie_esquema_contabil_w	:= 'N';
	ie_contab_ppcng_w	:= 'N';
end;

if (ie_esquema_contabil_w = 'S') then
	CALL pls_atualizar_codificacao_pck.pls_atualizar_codificacao(dt_referencia_w);
	qt_movimento_w := ctb_pls_atualizar_receita_in(nr_seq_lote_p, nr_seq_mensalidade_p, null, null, nm_usuario_p, cd_estabelecimento_p, null, 'P', qt_movimento_w);
	
	if (ie_contab_ppcng_w = 'S') and (ie_performance_contab_w = 'M') then
	
		open c_receita;
		loop
		fetch c_receita into
			nr_seq_mens_seg_w;
		EXIT WHEN NOT FOUND; /* apply on c_receita */
			begin
			
			nr_contador_w := nr_contador_w + 1;
			
			qt_movimento_w := ctb_pls_atualizar_receita_in(	null, null, null, null, nm_usuario_p, cd_estabelecimento_p, nr_seq_mens_seg_w, 'R', qt_movimento_w);

			if (coalesce(ie_commit_p,'S') = 'S') and (mod(nr_contador_w,1000) = 0) then
				commit;
			end if;			
				
			end;
		end loop;
		close c_receita;
	end if;
else
	CALL CTB_PLS_Atualizar_mensal(nr_seq_lote_p, nr_seq_mensalidade_p, nm_usuario_p, cd_estabelecimento_p);
end if;

ctb_pls_atualizar_imposto_in(nr_seq_lote_p, nr_seq_mensalidade_p, nm_usuario_p, cd_estabelecimento_p);

ie_geracao_p	:= 'S';

if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_contas_receita ( nr_seq_lote_p bigint, nr_seq_mensalidade_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commit_p text, ie_geracao_p INOUT text) FROM PUBLIC;

